mapper.select:
SELECT U_ICIN1.Secrecy, /*是否保密*/ U_ICIN1.Modle, /*需求型号*/ U_ICIN1.Brand, /*需求品牌*/ U_ICIN1.DemandQty, /*需求数量*/ U_ICIN1.ECCN, /*ECCN*/ U_ICIN1.Currency, /*货币*/ T_ICIN.InquiryDate, /*询价日期*/ T_ICIN.U_UserName, /*销售员姓名*/ T_ICIN.U_DeptName, /*销售部门*/ T_ICIN.U_CusGroupCode, /*客户性质名称*/ T_ICIN.U_CusLevel, /*客户等级名称*/ T_ICIN.U_DomainName /*客户行业名称*/ FROM U_ICIN1 LEFT JOIN T_ICIN ON U_ICIN1.DocEntry = T_ICIN.DocEntry WHERE Modle IN ( SELECT SDADA_2.sno FROM SDADA SDADA_1 LEFT JOIN SDADA SDADA_2 ON SDADA_2.TAG = SDADA_1.TAG WHERE SDADA_1.sno IN
'${item}'
) AND U_ICIN1.Secrecy = 'N' /*非保密*/
