SELECT SDADA_1.sno, /*型号*/ SDADA_1.QuoBrand, /*品牌*/ SDADA_1.package AS 'packages', /*封装*/ SDADA_1.packing, /*包装*/ SDADA_1.spq, /*SPQ*/ SDADA_1.datasheet, /*技术资料URL*/ SDADA_1.rohs, /*rohs*/ SDADA_1.msl_new, /*MSL-湿度等级*/ SDADA_1.ECCN, /*ECCN*/ SDADA_1.TAG, /*TAG*/ SDADA_1.type /*type*/ FROM SDADA SDADA_1 WHERE SDADA_1.TAG IN ( SELECT SDADA.TAG FROM SDADA WHERE SDADA.sno IN
'${item}'
)

DECLARE @sno AS VARCHAR(255);
SET @sno = #{sno};
SELECT *
FROM SDADA
WHERE sno IN (SELECT sno FROM SDADA WHERE sno LIKE '%' + @sno + '%');

DECLARE @sno AS VARCHAR(255);
SET @sno = #{sno};
SELECT
    *
FROM SDADA SDADA_1
WHERE SDADA_1.TAG IN (
    SELECT SDADA.TAG FROM SDADA WHERE SDADA.sno = @sno
)
