mapper.update:
UPDATE kc_now
SET kc_now.Modify = 'Y'
WHERE DocDate = #{da.docDate}
AND kc_now.Brand = #{da.brand}
AND kc_now.DocDate = #{da.docDate}

mapper.select:
DECLARE @PageSize INT; DECLARE @CurrentPage INT; SET @PageSize = ${sc.getSize}; /*每页显示数量*/ SET @CurrentPage = ${sc.getPage}; /*当前页码*/ SELECT TOP (@PageSize) * FROM ( SELECT T.*, ROW_NUMBER() over (ORDER BY T.DocDate) AS 'LineNum' FROM ( SELECT kc_now.DocDate, /*今日日期*/ kc_now.InDate, /*库存日期*/ kc_now.FrgnName, /*品名*/ kc_now.Brand, /*品牌*/ kc_now.ItemName, /*型号*/ kc_now.U_Batch, /*原批次*/ kc_now.Batch, /*批次*/ kc_now.Quantity, /*库存数量*/ kc_now.TypeCode, /*库存类型*/ kc_now.U_Secrecy, /*是否保密*/ kc_now.U_Price, /*库存成本价*/ Round(kc_now.LastSalePrice, 2) AS 'LastSalePrice', /*最近一次销售报价*/ kc_now.LastSaleDate, /*最近一次销售报价时间*/ Round(kc_now.LastPurchasePrice, 2) AS 'LastPurchasePrice', /*最近一次采购报价*/ kc_now.LastPurchaseDate, /*最近一次采购报价时间*/ kc_now.unit_price, /*云汉价*/ kc_now.unit_price_date, /*云汉价时间*/ kc_now.StockDays, /*库存天数*/ kc_now.Modify, /*是否人工修改*/ kc_now.kc_price7, /*库存定价(7天)*/ kc_now.reference7, /*七天定价参考*/ kc_now.kc_price , /*综合定价*/ kc_now.reference, /*参考价格*/ kc_now.kc_price_final , /*最终库存定价*/ kc_now.reference_final, /*最终参考价格*/ kc_now.price_2w , /*2w内单价*/ kc_now.quan_2w, /*2w区间*/ kc_now.price_2w_5w , /*2w-5w内单价*/ kc_now.quan_2w_5w, /*2w-5w区间*/ kc_now.price_5w_10w , /*5w-10w内单价*/ kc_now.quan_5w_10w, /*5w-10w区间*/ kc_now.price_10w , /*10w+单价*/ kc_now.quan_10w, /*10w+区间*/ T3.ConfirmDate AS 'LastPricingTime', /*最近一次定价时间*/ T3.kc_price_final AS 'LastPrice' /*最近一次定价单价*/ FROM kc_now LEFT JOIN ( SELECT T2.* FROM ( SELECT T0.Brand, T0.ItemName, MAX ( T0.ConfirmDate ) AS ConfirmDate FROM [dbo].[kc_now] T0 WHERE T0.ConfirmDate IS NOT NULL AND T0.Modify = 'Y' GROUP BY T0.Brand, T0.ItemName ) T1 LEFT JOIN [dbo].[kc_now] T2 ON T2.Brand = T1.Brand AND T2.ItemName = T1.ItemName AND T2.ConfirmDate = T1.ConfirmDate ) T3 ON T3.Brand = kc_now.Brand AND T3.ItemName = kc_now.ItemName
kc_now.DocDate IS NOT NULL
AND kc_now.TypeCode = #{sc.typeCode}
AND kc_now.StockDays >= #{sc.stockDays}
AND DateDiff(dd,kc_now.LastPurchaseDate,getdate()) > 7
AND kc_now.Brand = #{sc.brand}
AND kc_now.ItemName = #{sc.modle}
AND ( (kc_now.TypeCode = N'奇货可居' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 3 OR T3.ConfirmDate IS NULL)) OR (kc_now.TypeCode = N'特别关注' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 10 OR T3.ConfirmDate IS NULL)) OR (kc_now.TypeCode = N'热门型号' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 20 OR T3.ConfirmDate IS NULL)) OR (kc_now.TypeCode = N'普通库存' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 60 OR T3.ConfirmDate IS NULL)) OR (kc_now.TypeCode = N'呆滞物料' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 365 OR T3.ConfirmDate IS NULL)) )
AND ( /*差异大于10%, 缺少最近一次定价也视为差异大于10%*/ T3.kc_price_final IS NULL OR (kc_now.kc_price_final - T3.kc_price_final) /T3.kc_price_final >= 0.1 )
AND ( (kc_now.TypeCode = N'奇货可居' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 3 OR T3.ConfirmDate IS NULL)) OR (kc_now.TypeCode = N'特别关注' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 10 OR T3.ConfirmDate IS NULL)) OR (kc_now.TypeCode = N'热门型号' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 20 OR T3.ConfirmDate IS NULL)) OR (kc_now.TypeCode = N'普通库存' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 60 OR T3.ConfirmDate IS NULL)) OR (kc_now.TypeCode = N'呆滞物料' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 365 OR T3.ConfirmDate IS NULL)) ) AND ( T3.kc_price_final IS NULL OR (kc_now.kc_price_final - T3.kc_price_final) /T3.kc_price_final >= 0.1 )
AND kc_now.Modify = #{sc.modify}
AND DateDiff(dd,kc_now.DocDate,getdate())=0
) T )TT WHERE TT.LineNum > (@CurrentPage - 1) * @PageSize

SELECT COUNT(*) FROM kc_now LEFT JOIN ( SELECT T2.* FROM ( SELECT T0.Brand, T0.ItemName, MAX ( T0.ConfirmDate ) AS ConfirmDate FROM [dbo].[kc_now] T0 WHERE T0.ConfirmDate IS NOT NULL AND T0.Modify = 'Y' GROUP BY T0.Brand, T0.ItemName ) T1 LEFT JOIN [dbo].[kc_now] T2 ON T2.Brand = T1.Brand AND T2.ItemName = T1.ItemName AND T2.ConfirmDate = T1.ConfirmDate ) T3 ON T3.Brand = kc_now.Brand AND T3.ItemName = kc_now.ItemName
kc_now.DocDate IS NOT NULL
AND kc_now.TypeCode = #{sc.typeCode}
AND kc_now.StockDays >= #{sc.stockDays}
AND DateDiff(dd,kc_now.LastPurchaseDate,getdate()) > 7
AND kc_now.Brand = #{sc.brand}
AND kc_now.ItemName = #{sc.modle}
AND ( (kc_now.TypeCode = N'奇货可居' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 3 OR T3.ConfirmDate IS NULL)) OR (kc_now.TypeCode = N'特别关注' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 10 OR T3.ConfirmDate IS NULL)) OR (kc_now.TypeCode = N'热门型号' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 20 OR T3.ConfirmDate IS NULL)) OR (kc_now.TypeCode = N'普通库存' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 60 OR T3.ConfirmDate IS NULL)) OR (kc_now.TypeCode = N'呆滞物料' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 365 OR T3.ConfirmDate IS NULL)) )
AND ( T3.kc_price_final IS NULL OR (kc_now.kc_price_final - T3.kc_price_final) /T3.kc_price_final >= 0.1 )
AND ( (kc_now.TypeCode = N'奇货可居' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 3 OR T3.ConfirmDate IS NULL)) OR (kc_now.TypeCode = N'特别关注' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 10 OR T3.ConfirmDate IS NULL)) OR (kc_now.TypeCode = N'热门型号' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 20 OR T3.ConfirmDate IS NULL)) OR (kc_now.TypeCode = N'普通库存' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 60 OR T3.ConfirmDate IS NULL)) OR (kc_now.TypeCode = N'呆滞物料' AND (DateDiff(dd, T3.ConfirmDate, getdate()) >= 365 OR T3.ConfirmDate IS NULL)) ) AND ( T3.kc_price_final IS NULL OR (kc_now.kc_price_final - T3.kc_price_final) /T3.kc_price_final >= 0.1 )
AND kc_now.Modify = #{sc.modify}
AND DateDiff(dd,kc_now.DocDate,getdate())=0
